from flet import *

class ProcessButton(ElevatedButton):
    def __init__(self, text, icon=icons.EDIT_DOCUMENT, on_click=None):
        super().__init__(text=text)
        self.width = 600
        self.icon_name = icon
        self.on_click = on_click
        self._initialize_button()

    def _initialize_button(self):
        self.content = Row(
            [
                Icon(name=self.icon_name, color="#c55b36"),  
                Text(value=self.text, size=21, weight=FontWeight.W_700)
            ],
            alignment=MainAxisAlignment.CENTER,
        )
        self.bgcolor = "#f2eee5"
        self.color = "#5e5a5a"
        self.width = self.width
        self.height = "55"
        self.style = ButtonStyle(
            overlay_color="#ffa88a", shape=RoundedRectangleBorder(radius=10),
        )
        self.elevation = 3

class BaseScreen(View):
    def __init__(self):
        super().__init__()
        self.route='/',
        self.bgcolor="#f8faf7"
        self.horizontal_alignment="center"
        self.vertical_alignment="center"
        self._initialize_screen()
    
    def _initialize_screen(self):

        img = Row([Image(
            src_base64="",
            width=100,
            height=45,
            fit=ImageFit.CONTAIN,
        )], alignment=MainAxisAlignment.END)

        self.appbar = AppBar(
            leading=img,
            leading_width=120,
            title=Text("Simple.Kit"),
            center_title=False,
            bgcolor="#c55b36",
            color="white",
            actions=[
                PopupMenuButton(
                    items=[
                        PopupMenuItem(text="Настройки"),
                        PopupMenuItem(),  # divider
                        PopupMenuItem(text="Инфо"),
                        PopupMenuItem(),  # divider
                        PopupMenuItem(text="FAQ"),
                        PopupMenuItem(),  # divider
                        PopupMenuItem(text="Обновить конфигурацию")
                    ]
                ),
            ],
        )
        
        self.navigation_bar = NavigationBar(
                destinations=[
                    NavigationDestination(icon_content=Icon(name=icons.APPS, color="white"),selected_icon_content=None),
                    NavigationDestination(icon_content=Icon(name=icons.HOME, color="white")),
                    NavigationDestination(icon_content=Icon(name=icons.CHAT, color="white"))
                ],
                bgcolor="#c55b36",
                on_change=self._nav_handler
            )
    
    def _nav_handler(self, e):
        routs = ('/', '/', '/')
        self.page.go(routs[e.control.selected_index])    
